def safe_divide(a, b):
    """Divide a by b, return None if error"""
    try:
        return a / b
    except (ZeroDivisionError, TypeError):
        return None

def safe_int_convert(value):
    """Convert to int, return 0 if error"""
    try:
        return int(value)
    except (ValueError, TypeError):
        return 0

def safe_list_access(lst, index):
    """Get list item at index, return None if error"""
    try:
        return lst[index]
    except (IndexError, TypeError):
        return None

def safe_dict_get(dictionary, key, default=None):
    """Get dict value, return default if key missing"""
    try:
        return dictionary[key]
    except (KeyError, TypeError):
        return default
