def safe_convert(value, target_type):
    """
    Safely convert value to target_type.
    Returns converted value or None if conversion fails.
    """
    try:
        if target_type == 'int':
            return int(float(value))  # Handle '42.5' -> 42
        elif target_type == 'float':
            return float(value)
        elif target_type == 'str':
            return str(value)
        elif target_type == 'bool':
            if isinstance(value, str):
                return value.lower() in ('true', '1', 'yes')
            return bool(value)
        else:
            return None
    except (ValueError, TypeError):
        return None

# Test cases
print(safe_convert('42', 'int'))
print(safe_convert('3.14', 'float'))
print(safe_convert('hello', 'int'))
print(safe_convert('True', 'bool'))
