def safe_int_conversion(value):
    """Convert value to int, return None if conversion fails"""
    try:
        return int(value)
    except (ValueError, TypeError):
        return None

def is_numeric(value):
    """Check if value is int or float"""
    return isinstance(value, (int, float))

def convert_temperature(temp_str):
    """Convert string temperature to float, add 32, return as string"""
    temp_float = float(temp_str)
    result = temp_float + 32
    return str(result)

def get_type_name(value):
    """Return the type name as a string (e.g., 'int', 'str')"""
    return type(value).__name__
