def create_profile(name, age, city="Unknown", **additional):
    """Create user profile with required and optional fields"""
    profile = {
        "name": name,
        "age": age,
        "city": city
    }
    profile.update(additional)
    return profile

def sum_numbers(*numbers):
    """Sum any number of arguments"""
    return sum(numbers)

def format_message(message, *tags, prefix="INFO"):
    """Format message with tags and prefix"""
    tag_str = ",".join(tags) if tags else ""
    if tag_str:
        return f"[{prefix}] [{tag_str}] {message}"
    return f"[{prefix}] {message}"
