def square_all(numbers):
    """Use map and lambda to square all numbers"""
    return list(map(lambda x: x ** 2, numbers))

def filter_evens(numbers):
    """Use filter and lambda to get even numbers"""
    return list(filter(lambda x: x % 2 == 0, numbers))

def sort_by_length(words):
    """Use sorted and lambda to sort by word length"""
    return sorted(words, key=lambda w: len(w))

def sort_by_age(users):
    """Sort list of user dicts by age using lambda"""
    return sorted(users, key=lambda u: u["age"])
