def make_multiplier(factor):
    """Return function that multiplies by factor"""
    def multiply(n):
        return n * factor
    return multiply

def make_counter(start=0, step=1):
    """Return counter function that increments by step"""
    count = start

    def counter():
        nonlocal count
        current = count
        count += step
        return current

    return counter

def create_greeter(greeting):
    """Return function that greets with custom greeting"""
    def greet(name):
        return f"{greeting}, {name}!"
    return greet
