def is_in_range(number, min_val, max_val):
    """Return True if number is between min and max (inclusive)"""
    return min_val <= number <= max_val

def compare_strings(str1, str2):
    """Return 'equal', 'first', or 'second' based on comparison"""
    if str1 == str2:
        return "equal"
    elif str1 < str2:
        return "first"
    else:
        return "second"

def is_valid_percentage(value):
    """Return True if value is between 0 and 100 (inclusive)"""
    return 0 <= value <= 100
