def is_in_range(num, min_val, max_val):
    """Check if number is within range (inclusive)."""
    return min_val <= num <= max_val

def categorize_age(age):
    """Categorize age into groups."""
    if age <= 12:
        return 'child'
    elif age <= 19:
        return 'teen'
    elif age <= 64:
        return 'adult'
    else:
        return 'senior'

def check_temperature(temp):
    """Check temperature safety level (Celsius)."""
    if temp < 10:
        return 'cold'
    elif temp < 20:
        return 'cool'
    elif temp <= 25:
        return 'comfortable'
    elif temp <= 30:
        return 'warm'
    else:
        return 'hot'

def compare_scores(score1, score2):
    """Compare two scores and return result with difference."""
    difference = abs(score1 - score2)

    if score1 > score2:
        return ('score1 is higher', difference)
    elif score1 < score2:
        return ('score1 is lower', difference)
    else:
        return ('scores are equal', 0)

# Test the functions
print("Range Validation:")
print("="*60)
test_ranges = [
    (5, 1, 10),
    (0, 1, 10),
    (10, 1, 10),
    (15, 1, 10),
    (-5, -10, 0)
]

for num, min_val, max_val in test_ranges:
    result = is_in_range(num, min_val, max_val)
    print(f"{num} in range [{min_val}, {max_val}]: {result}")

print("\n" + "="*60)
print("Age Categories:")
print("="*60)
test_ages = [5, 12, 13, 19, 20, 35, 64, 65, 80]

for age in test_ages:
    category = categorize_age(age)
    print(f"Age {age}: {category}")

print("\n" + "="*60)
print("Temperature Check:")
print("="*60)
test_temps = [5, 10, 15, 20, 25, 28, 30, 35]

for temp in test_temps:
    level = check_temperature(temp)
    print(f"{temp}°C: {level}")

print("\n" + "="*60)
print("Score Comparison:")
print("="*60)
test_scores = [(85, 90), (95, 90), (88, 88), (70, 85)]

for s1, s2 in test_scores:
    result, diff = compare_scores(s1, s2)
    print(f"Score1: {s1}, Score2: {s2} → {result} (diff: {diff})")
