def calculate_total(prices):
    """Calculate sum of all prices."""
    total = 0
    for price in prices:
        total += price
    return total

def count_vowels(text):
    """Count number of vowels in text."""
    vowels = 'aeiouAEIOU'
    count = 0
    for char in text:
        if char in vowels:
            count += 1
    return count

def filter_even_numbers(numbers):
    """Return list of even numbers."""
    evens = []
    for num in numbers:
        if num % 2 == 0:
            evens.append(num)
    return evens

def pair_items(names, scores):
    """Pair names with scores using zip."""
    pairs = []
    for name, score in zip(names, scores):
        pairs.append((name, score))
    return pairs

def find_max_with_index(numbers):
    """Find maximum value and its index."""
    if not numbers:
        return (None, None)

    max_val = numbers[0]
    max_idx = 0

    for i, num in enumerate(numbers):
        if num > max_val:
            max_val = num
            max_idx = i

    return (max_val, max_idx)

def generate_multiplication_table(n):
    """Generate n×n multiplication table as 2D list."""
    table = []
    for i in range(1, n + 1):
        row = []
        for j in range(1, n + 1):
            row.append(i * j)
        table.append(row)
    return table

