def sum_numbers(numbers):
    """Return sum of all numbers in list"""
    total = 0
    for num in numbers:
        total += num
    return total

def count_evens(numbers):
    """Count how many even numbers in list"""
    count = 0
    for num in numbers:
        if num % 2 == 0:
            count += 1
    return count

def reverse_list(items):
    """Return new list with items in reverse order"""
    reversed_items = []
    for item in items:
        reversed_items.insert(0, item)
    return reversed_items

def create_pairs(list1, list2):
    """Return list of tuples pairing corresponding elements"""
    pairs = []
    for item1, item2 in zip(list1, list2):
        pairs.append((item1, item2))
    return pairs
