def countdown(n):
    """Return list counting down from n to 1"""
    result = []
    while n > 0:
        result.append(n)
        n -= 1
    return result

def find_first_divisible(numbers, divisor):
    """Return first number divisible by divisor, or None if not found"""
    for num in numbers:
        if num % divisor == 0:
            return num
    return None

def sum_until_negative(numbers):
    """Sum numbers until negative number encountered"""
    total = 0
    for num in numbers:
        if num < 0:
            break
        total += num
    return total

def skip_multiples_of_three(n):
    """Return list from 1 to n, skipping multiples of 3"""
    result = []
    i = 1
    while i <= n:
        if i % 3 != 0:
            result.append(i)
        i += 1
    return result
