def get_middle_element(items):
    """Return the middle element of a list"""
    if len(items) == 0:
        return None
    mid = len(items) // 2
    return items[mid]

def swap_first_and_last(items):
    """Swap first and last elements, return modified list"""
    if len(items) < 2:
        return items
    items[0], items[-1] = items[-1], items[0]
    return items

def contains_duplicate(items):
    """Check if list contains any duplicate elements"""
    for i, item in enumerate(items):
        if item in items[i+1:]:
            return True
    return False

def safe_get(items, index, default=None):
    """Safely get element at index, return default if out of range"""
    if -len(items) <= index < len(items):
        return items[index]
    return default
