def get_first_and_last(items):
    """Return tuple of (first_item, last_item)."""
    if not items:
        return (None, None)
    return (items[0], items[-1])

def update_price(prices, index, new_price):
    """Update price at index and return updated list."""
    if 0 <= index < len(prices) or -len(prices) <= index < 0:
        prices[index] = new_price
    return prices

def is_in_stock(inventory, item):
    """Check if item exists in inventory."""
    return item in inventory

def get_middle_element(items):
    """Return middle element of list."""
    if not items:
        return None
    middle_index = len(items) // 2
    return items[middle_index]

def swap_elements(items, idx1, idx2):
    """Swap elements at idx1 and idx2."""
    if (0 <= idx1 < len(items) and 0 <= idx2 < len(items)):
        items[idx1], items[idx2] = items[idx2], items[idx1]
    return items

def count_items_over_price(prices, threshold):
    """Count how many prices exceed threshold."""
    count = 0
    for price in prices:
        if price > threshold:
            count += 1
    return count

