def remove_all_occurrences(items, value):
    """Remove all occurrences of value from list"""
    while value in items:
        items.remove(value)
    return items

def insert_sorted(sorted_list, value):
    """Insert value into sorted list maintaining order"""
    for i, item in enumerate(sorted_list):
        if value <= item:
            sorted_list.insert(i, value)
            return sorted_list
    sorted_list.append(value)
    return sorted_list

def rotate_left(items, n=1):
    """Rotate list left by n positions"""
    if len(items) == 0:
        return items
    n = n % len(items)
    for _ in range(n):
        first = items.pop(0)
        items.append(first)
    return items

def get_unique_sorted(items):
    """Return sorted list with duplicates removed"""
    unique = []
    for item in items:
        if item not in unique:
            unique.append(item)
    unique.sort()
    return unique
