def get_every_nth(items, n):
    """Return every nth element from list"""
    return items[::n]

def split_at_index(items, index):
    """Split list at index, return tuple of (before, after)"""
    return (items[:index], items[index:])

def reverse_middle(items):
    """Reverse only middle half of list"""
    length = len(items)
    quarter = length // 4
    three_quarter = length - quarter
    items[quarter:three_quarter] = items[quarter:three_quarter][::-1]
    return items

def remove_edges(items, n):
    """Remove n elements from both start and end"""
    if n >= len(items) // 2:
        return []
    return items[n:-n]
