def sum_all(*args):
    """Sum any number of arguments.
    Returns 0 if no arguments.
    """
    return sum(args)

def build_profile(name, **info):
    """Build user profile dictionary.
    name is required, all other fields via **kwargs.
    Returns dict with name and all info fields.
    """
    profile = {'name': name}
    profile.update(info)
    return profile

def greet(greeting, *names):
    """Greet multiple people.
    Returns list of greeting strings.
    """
    return [f"{greeting}, {name}!" for name in names]

def create_url(base, path, **params):
    """Build URL with query parameters.
    base: base URL (e.g., 'https://api.example.com')
    path: endpoint path (e.g., 'users')
    params: query parameters as keyword arguments
    Returns full URL with query string.
    """
    url = f"{base}/{path}"
    if params:
        query = "&".join(f"{k}={v}" for k, v in params.items())
        url = f"{url}?{query}"
    return url

def flexible_math(operation, *numbers):
    """Apply operation to all numbers.
    operation: 'sum', 'product', 'max', 'min'
    numbers: variable number of values
    Returns result of operation.
    """
    if not numbers:
        return None

    if operation == 'sum':
        return sum(numbers)
    elif operation == 'product':
        result = 1
        for num in numbers:
            result *= num
        return result
    elif operation == 'max':
        return max(numbers)
    elif operation == 'min':
        return min(numbers)
    else:
        return None

def merge_dicts(**dicts):
    """Merge multiple dictionaries.
    Takes any number of keyword arguments (each is a dict).
    Returns single merged dictionary.
    """
    merged = {}
    for key, value in dicts.items():
        if isinstance(value, dict):
            merged.update(value)
    return merged

