def safe_convert(value, target_type):
    """Safely convert value to target type.
    target_type: 'int', 'float', 'str', 'bool'
    Returns converted value or None if conversion fails.
    """
    try:
        if target_type == 'int':
            return int(value)
        elif target_type == 'float':
            return float(value)
        elif target_type == 'str':
            return str(value)
        elif target_type == 'bool':
            return bool(value)
    except (ValueError, TypeError):
        return None

def calculate_stats(numbers):
    """Calculate statistics using built-in functions.
    Returns dict with min, max, sum, average, count.
    """
    if not numbers:
        return None
    return {
        'min': min(numbers),
        'max': max(numbers),
        'sum': sum(numbers),
        'average': sum(numbers) / len(numbers),
        'count': len(numbers)
    }

def pair_lists(keys, values):
    """Create dictionary from two lists using zip.
    Returns dict pairing keys with values.
    """
    return dict(zip(keys, values))

def check_valid_scores(scores, min_score=0, max_score=100):
    """Check if all scores are in valid range.
    Uses all() to verify all scores pass.
    """
    return all(min_score <= score <= max_score for score in scores)

def has_passing_grade(scores, passing=60):
    """Check if any score is passing.
    Uses any() to check if at least one passes.
    """
    return any(score >= passing for score in scores)

def process_names(names):
    """Process list of names.
    Returns dict with:
    - 'sorted': alphabetically sorted names
    - 'numbered': list of "1. Name" strings
    - 'lengths': list of name lengths
    """
    return {
        'sorted': sorted(names),
        'numbered': [f"{i}. {name}" for i, name in enumerate(names, start=1)],
        'lengths': [len(name) for name in names]
    }

