def apply_de_morgan_and(a, b):
    """Apply De Morgan's Law: not (a and b) = (not a) or (not b)."""
    original = not (a and b)
    simplified = (not a) or (not b)
    return (original, simplified, original == simplified)

def apply_de_morgan_or(a, b):
    """Apply De Morgan's Law: not (a or b) = (not a) and (not b)."""
    original = not (a or b)
    simplified = (not a) and (not b)
    return (original, simplified, original == simplified)

def simplify_double_negation(value):
    """Simplify double negation: not not A = A."""
    double_negated = not not value
    return (double_negated, value, double_negated == value)

def check_tautology(a, operation):
    """Check if expression is tautology (always True)."""
    if operation == 'or_complement':
        # A or not A is always True
        return a or not a
    return False

def check_contradiction(a, operation):
    """Check if expression is contradiction (always False)."""
    if operation == 'and_complement':
        # A and not A is always False
        result = a and not a
        return result == False
    elif operation == 'false_and':
        # A and False is always False
        result = a and False
        return result == False
    return False

def generate_truth_table(operation):
    """Generate truth table for binary operation."""
    results = []

    for a in [False, True]:
        for b in [False, True]:
            if operation == 'and':
                result = a and b
            elif operation == 'or':
                result = a or b
            elif operation == 'xor':
                result = a != b
            elif operation == 'implies':
                result = (not a) or b
            elif operation == 'nand':
                result = not (a and b)
            elif operation == 'nor':
                result = not (a or b)
            else:
                result = None

            results.append((a, b, result))

    return results

def simplify_expression(expr_type, a, b, c=None):
    """Simplify common expression patterns."""
    if expr_type == 'absorption_or':
        # A or (A and B) = A
        original = a or (a and b)
        simplified = a
        return (original, simplified, original == simplified)

    elif expr_type == 'absorption_and':
        # A and (A or B) = A
        original = a and (a or b)
        simplified = a
        return (original, simplified, original == simplified)

    elif expr_type == 'distributive':
        # A and (B or C) = (A and B) or (A and C)
        original = a and (b or c)
        simplified = (a and b) or (a and c)
        return (original, simplified, original == simplified)

    elif expr_type == 'idempotent_or':
        # A or A = A
        original = a or b  # b is same as a
        simplified = a
        return (original, simplified, original == simplified)

    elif expr_type == 'idempotent_and':
        # A and A = A
        original = a and b  # b is same as a
        simplified = a
        return (original, simplified, original == simplified)

    return (None, None, False)

