def print_multiplication_table(n):
    """Generate n×n multiplication table as 2D list."""
    table = []
    for i in range(1, n + 1):
        row = []
        for j in range(1, n + 1):
            row.append(i * j)
        table.append(row)
    return table

def sum_matrix(matrix):
    """Sum all elements in matrix."""
    total = 0
    for row in matrix:
        for element in row:
            total += element
    return total

def find_in_matrix(matrix, target):
    """Find position (row, col) of target, or None."""
    for i in range(len(matrix)):
        for j in range(len(matrix[i])):
            if matrix[i][j] == target:
                return (i, j)
    return None

def transpose_matrix(matrix):
    """Transpose matrix (swap rows and columns)."""
    if not matrix:
        return []

    rows = len(matrix)
    cols = len(matrix[0])
    transposed = []

    for j in range(cols):
        new_row = []
        for i in range(rows):
            new_row.append(matrix[i][j])
        transposed.append(new_row)

    return transposed

def generate_triangle(rows):
    """Generate number triangle pattern."""
    result = []
    for i in range(1, rows + 1):
        row = []
        for j in range(i):
            row.append(i)
        result.append(row)
    return result

def multiply_matrices(m1, m2):
    """Multiply two matrices."""
    if not m1 or not m2:
        return None

    if len(m1[0]) != len(m2):
        return None

    result = []
    for i in range(len(m1)):
        row = []
        for j in range(len(m2[0])):
            sum_val = 0
            for k in range(len(m2)):
                sum_val += m1[i][k] * m2[k][j]
            row.append(sum_val)
        result.append(row)

    return result

