def evens_up_to(n):
    """Generate list of even numbers from 0 to n (inclusive)."""
    return list(range(0, n + 1, 2))

def countdown_list(start):
    """Generate countdown from start to 1."""
    return list(range(start, 0, -1))

def multiples_of(number, count):
    """Get first count multiples of number."""
    return list(range(number, number * (count + 1), number))

def range_sum(start, stop, step):
    """Calculate sum of range(start, stop, step)."""
    return sum(range(start, stop, step))

def skip_count(start, stop, skip):
    """Generate sequence skipping every skip numbers."""
    return list(range(start, stop, skip))

def reverse_indices(length):
    """Generate reverse indices for list of given length."""
    return list(range(length - 1, -1, -1))

