def number_items(items):
    """Return list of numbered items ('1. item', '2. item', ...)."""
    result = []
    for i, item in enumerate(items, start=1):
        result.append(f"{i}. {item}")
    return result

def pair_lists(list1, list2):
    """Zip two lists into list of tuples."""
    return list(zip(list1, list2))

def create_dict_from_lists(keys, values):
    """Create dictionary from parallel lists."""
    return dict(zip(keys, values))

def find_all_positions(items, target):
    """Find all indices where target appears."""
    positions = []
    for i, item in enumerate(items):
        if item == target:
            positions.append(i)
    return positions

def compare_lists(list1, list2):
    """Compare lists element-by-element, return list of comparisons."""
    comparisons = []
    for i, (a, b) in enumerate(zip(list1, list2)):
        if a == b:
            comparisons.append((i, '✓', a, b))
        else:
            comparisons.append((i, '✗', a, b))
    return comparisons

def unzip_pairs(pairs):
    """Unzip list of pairs into two lists."""
    if not pairs:
        return ([], [])
    list1, list2 = zip(*pairs)
    return (list(list1), list(list2))

