def squares_of_evens(numbers):
    """Return squares of even numbers only"""
    return [x ** 2 for x in numbers if x % 2 == 0]

def word_lengths(words, min_length=0):
    """Return lengths of words that are >= min_length"""
    return [len(word) for word in words if len(word) >= min_length]

def flatten_matrix(matrix):
    """Flatten 2D matrix into 1D list"""
    return [num for row in matrix for num in row]

def create_grid(rows, cols):
    """Create 2D grid filled with zeros"""
    return [[0 for _ in range(cols)] for _ in range(rows)]
