def merge_dicts(dict1, dict2):
    """Merge two dictionaries, dict2 overrides dict1"""
    result = dict1.copy()
    result.update(dict2)
    return result

def safe_divide(data, key1, key2):
    """Safely divide data[key1] by data[key2], return 0 if keys missing"""
    val1 = data.get(key1)
    val2 = data.get(key2)
    if val1 is None or val2 is None or val2 == 0:
        return 0
    return val1 / val2

def get_nested_value(data, keys):
    """Get nested value using list of keys, return None if path doesn't exist"""
    for key in keys:
        if isinstance(data, dict) and key in data:
            data = data[key]
        else:
            return None
    return data

def invert_dict(d):
    """Swap keys and values (assume values are unique)"""
    return {v: k for k, v in d.items()}
