def square_dict(n):
    """Create dict mapping 1 to n to their squares"""
    return {x: x ** 2 for x in range(1, n + 1)}

def filter_by_value(d, min_value):
    """Return dict with only items where value >= min_value"""
    return {k: v for k, v in d.items() if v >= min_value}

def uppercase_keys(d):
    """Return new dict with all string keys uppercased"""
    return {k.upper() if isinstance(k, str) else k: v for k, v in d.items()}

def group_by_length(words):
    """Group words by length: {length: [words]}"""
    result = {}
    for word in words:
        length = len(word)
        if length not in result:
            result[length] = []
        result[length].append(word)
    return result
