def get_key_by_value(d, target_value):
    """Return first key with given value, or None if not found"""
    for key, value in d.items():
        if value == target_value:
            return key
    return None

def remove_keys(d, keys_to_remove):
    """Remove multiple keys from dictionary, return modified dict"""
    for key in keys_to_remove:
        d.pop(key, None)
    return d

def count_values(d):
    """Count occurrences of each value in dictionary"""
    counts = {}
    for value in d.values():
        counts[value] = counts.get(value, 0) + 1
    return counts

def merge_sum_values(dict1, dict2):
    """Merge dicts, summing values for common keys"""
    result = dict1.copy()
    for key, value in dict2.items():
        result[key] = result.get(key, 0) + value
    return result
