def safe_int(value, default=0):
    """Convert value to int, return default if conversion fails"""
    try:
        return int(value)
    except (ValueError, TypeError):
        return default

def safe_divide(a, b):
    """Divide a by b, return None if error occurs"""
    try:
        return a / b
    except (ZeroDivisionError, TypeError):
        return None

def get_dict_value(d, key, default=None):
    """Get value from dict, return default if key not found"""
    try:
        return d[key]
    except (KeyError, TypeError):
        return default

def safe_list_get(items, index, default=None):
    """Get item from list, return default if index invalid"""
    try:
        return items[index]
    except (IndexError, TypeError):
        return default
