def get_unique_elements(items):
    """Return list of unique elements maintaining order"""
    seen = set()
    result = []
    for item in items:
        if item not in seen:
            seen.add(item)
            result.append(item)
    return result

def find_common(list1, list2):
    """Return list of common elements"""
    return list(set(list1) & set(list2))

def find_missing_numbers(numbers, start, end):
    """Find missing numbers in range [start, end]"""
    full_range = set(range(start, end + 1))
    present = set(numbers)
    return sorted(full_range - present)

def has_duplicate(items):
    """Check if list contains any duplicates"""
    return len(items) != len(set(items))
