def count_words(text):
    """Return dictionary of word counts"""
    words = text.lower().split()
    counts = {}
    for word in words:
        counts[word] = counts.get(word, 0) + 1
    return counts

def invert_dict(d):
    """Return dictionary with keys and values swapped"""
    return {value: key for key, value in d.items()}

def merge_dicts(dict1, dict2):
    """Merge two dictionaries, dict2 values override dict1"""
    result = dict1.copy()
    result.update(dict2)
    return result

def get_top_score(scores):
    """Return name with highest score"""
    if not scores:
        return None
    return max(scores, key=scores.get)
