# Simplify this overcomplicated function
def is_even(number):
    if number % 2 == 0:
        result = True
    else:
        result = False

    if result == True:
        return True
    elif result == False:
        return False

# Test your simplified function
print(is_even(4))
print(is_even(7))
print(is_even(0))
