# Rectangle class with instance methods
class Rectangle:
    def __init__(self, width, height):
        self.width = width
        self.height = height

    def area(self):
        return self.width * self.height

    def perimeter(self):
        return 2 * (self.width + self.height)

# Test
rect = Rectangle(5, 10)
print(rect.area())
print(rect.perimeter())
