class Engine:
    def __init__(self, engine_type, horsepower):
        self.engine_type = engine_type
        self.horsepower = horsepower

    def start(self):
        print(f"{self.engine_type} engine started with {self.horsepower} HP")

class Car:
    def __init__(self, brand, engine):
        self.brand = brand
        self.engine = engine

    def start(self):
        print(f"{self.brand} car is starting...")
        self.engine.start()

# Test
engine = Engine("V8", 400)
car = Car("Ford", engine)
car.start()
