solution:
class DataStore:
    def __init__(self):
        self.__data = {}

    def save(self, key, value):
        self.__data[key] = value

    def load(self, key):
        return self.__data.get(key, None)

store = DataStore()
store.save("name", "Alice")
print(store.load("name"))
