solution:
class Product:
    def __init__(self, name, price):
        self.name = name
        self._price = 0
        self.price = price

    @property
    def price(self):
        return self._price

    @price.setter
    def price(self, value):
        if value < 0:
            raise ValueError("Price cannot be negative")
        self._price = value

product = Product("Widget", 9.99)
print(product.price)
