solution:
from abc import ABC, abstractmethod

class Animal(ABC):
    @abstractmethod
    def make_sound(self):
        pass

    @abstractmethod
    def move(self):
        pass

class Dog(Animal):
    def make_sound(self):
        print("Woof!")

    def move(self):
        print("Running on four legs")

dog = Dog()
dog.make_sound()
dog.move()
