from abc import ABC, abstractmethod

class Vehicle(ABC):
    @abstractmethod
    def start(self):
        pass

class Car(Vehicle):
    def start(self):
        print("Car engine started")

class Motorcycle(Vehicle):
    def start(self):
        print("Motorcycle engine started")

car = Car()
motorcycle = Motorcycle()
car.start()
motorcycle.start()
