solution:
from abc import ABC, abstractmethod

class DataProcessor(ABC):
    def process(self):
        self.load_data()
        self.transform_data()
        self.save_data()

    @abstractmethod
    def load_data(self):
        pass

    @abstractmethod
    def transform_data(self):
        pass

    @abstractmethod
    def save_data(self):
        pass

class CSVProcessor(DataProcessor):
    def load_data(self):
        print("Loading CSV data")

    def transform_data(self):
        print("Transforming CSV data")

    def save_data(self):
        print("Saving CSV data")

processor = CSVProcessor()
processor.process()
