solution:
from abc import ABC, abstractmethod

class Database(ABC):
    @abstractmethod
    def connect(self):
        pass

    @abstractmethod
    def query(self, sql):
        pass

class PostgreSQL(Database):
    def connect(self):
        print("Connected to PostgreSQL")

    def query(self, sql):
        return f"Executing: {sql}"

db = PostgreSQL()
db.connect()
print(db.query("SELECT * FROM users"))
