solution:
from abc import ABC, abstractmethod

class Plugin(ABC):
    @abstractmethod
    def execute(self):
        pass

class LoggerPlugin(Plugin):
    def execute(self):
        print("Logger plugin executing")

class CachePlugin(Plugin):
    def execute(self):
        print("Cache plugin executing")

class PluginManager:
    def __init__(self):
        self.plugins = []

    def register(self, plugin):
        self.plugins.append(plugin)

    def run_all(self):
        for plugin in self.plugins:
            plugin.execute()

manager = PluginManager()
manager.register(LoggerPlugin())
manager.register(CachePlugin())
manager.run_all()
