solution:
class Person:
    def __init__(self, name):
        self.name = name

class Employee(Person):
    def __init__(self, name, employee_id):
        super().__init__(name)
        self.employee_id = employee_id

emp = Employee("Alice", "E123")
print(emp.name)
print(emp.employee_id)
