solution:
class Logger:
    def log(self, message):
        print(f"LOG: {message}")

class TimestampLogger(Logger):
    def log(self, message):
        super().log(message)
        print("[2024-01-15 10:30:00]")

logger = TimestampLogger()
logger.log("Error occurred")
