solution:
class TimestampMixin:
    def get_timestamp(self):
        return "2024-01-15"

class SerializeMixin:
    def to_dict(self):
        return {"data": "serialized"}

class User(TimestampMixin, SerializeMixin):
    pass

user = User()
print(user.get_timestamp())
print(user.to_dict())
