solution:
class Bird:
    def fly(self):
        return "Flying"

class Sparrow(Bird):
    def fly(self):
        return "Sparrow flying"

def make_bird_fly(bird):
    return bird.fly()

sparrow = Sparrow()
print(make_bird_fly(sparrow))
