solution:
from abc import ABC, abstractmethod
from typing import List

class Animal(ABC):
    @abstractmethod
    def speak(self) -> str:
        pass

class Dog(Animal):
    def speak(self) -> str:
        return "Woof!"

class Cat(Animal):
    def speak(self) -> str:
        return "Meow!"

def make_animals_speak(animals: List[Animal]) -> None:
    for animal in animals:
        print(animal.speak())

animals = [Dog(), Cat()]
make_animals_speak(animals)
