solution:
from abc import ABC, abstractmethod

class Discount(ABC):
    @abstractmethod
    def calculate(self, price):
        pass

class PercentageDiscount(Discount):
    def __init__(self, percent):
        self.percent = percent

    def calculate(self, price):
        return price * (self.percent / 100)

class FixedDiscount(Discount):
    def __init__(self, amount):
        self.amount = amount

    def calculate(self, price):
        return self.amount

class PriceCalculator:
    def calculate_final_price(self, price, discount):
        return price - discount.calculate(price)

calc = PriceCalculator()
print(calc.calculate_final_price(100, PercentageDiscount(10)))
print(calc.calculate_final_price(100, FixedDiscount(15)))
