solution:
from abc import ABC, abstractmethod

class Validator(ABC):
    @abstractmethod
    def validate(self, value):
        pass

class EmailValidator(Validator):
    def validate(self, value):
        return "@" in value

class PhoneValidator(Validator):
    def validate(self, value):
        return len(value) == 10

def test_validators():
    validators = [EmailValidator(), PhoneValidator()]
    test_cases = [("test@email.com", True), ("1234567890", True)]

    for i, validator in enumerate(validators):
        value, expected = test_cases[i]
        result = validator.validate(value)
        assert result == expected, f"Failed for {validator.__class__.__name__}"
        print(f"{validator.__class__.__name__}: passed")

test_validators()
